		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (22, '2004-01-11', 'Punkteteilung von Kriegshaber III in Gersthofen', 'Herausragende Leistung von Peter am Spitzenbrett', 'Eckhardt Frank', NULL, '<TABLE border=0 cellpadding=3 cellspacing=0><TR><TD colspan=7 bgcolor=000000></TD></TR><TR><TD colspan=7 bgcolor=ffffff>Einzelergebnisse</TD></TR><TR><TD colspan=7 bgcolor=000000></TD></TR><TR bgcolor=dddddd><TD></TD><TD width=150>SK Gersthofen</TD><TD width=50>1816</TD><TD></TD><TD width=150>SK Kriegshaber III</TD><TD width=50>1753</TD><TD width=80>4-4</TD></TR>\r\n\r\n<TR>\r\n<TD>1</TD>\r\n<TD>Franz Leipold</TD>\r\n<TD>2114</TD>\r\n<TD>-</TD>\r\n<TD>Peter Reichardt</TD>\r\n<TD>1799</TD>\r\n<TD>0-1</TD>\r\n</TR>\r\n\r\n<TR>\r\n<TD>2</TD>\r\n<TD>Yakiv Men</TD>\r\n<TD>1950</TD>\r\n<TD>-</TD>\r\n<TD>Thomas Städele</TD>\r\n<TD>1879</TD>\r\n<TD>0-1</TD>\r\n</TR>\r\n\r\n<TR>\r\n<TD>3</TD>\r\n<TD>Robert Pauler</TD>\r\n<TD>1942</TD>\r\n<TD>-</TD>\r\n<TD>Lothar Sepp</TD>\r\n<TD>1738</TD>\r\n<TD>1-0</TD>\r\n</TR>\r\n\r\n<TR>\r\n<TD>4</TD>\r\n<TD>Konrad Liepert</TD>\r\n<TD>1911</TD>\r\n<TD>-</TD>\r\n<TD>Detlef Czajka</TD>\r\n<TD>1801</TD>\r\n<TD>½-½</TD>\r\n</TR>\r\n\r\n<TR>\r\n<TD>5</TD>\r\n<TD>Guido Band</TD>\r\n<TD>1788</TD>\r\n<TD>-</TD>\r\n<TD>Peter Grabowski</TD>\r\n<TD>1728</TD>\r\n<TD>½-½</TD>\r\n</TR>\r\n\r\n<TR>\r\n<TD>6</TD>\r\n<TD>Thomas Spanier</TD>\r\n<TD>1632</TD>\r\n<TD>-</TD>\r\n<TD>Stefan Kiechl</TD>\r\n<TD>1705</TD>\r\n<TD>½-½</TD>\r\n</TR>\r\n\r\n<TR>\r\n<TD>7</TD>\r\n<TD>Alfred Stahl</TD>\r\n<TD>1663</TD>\r\n<TD>-</TD>\r\n<TD>Achim Margraf</TD>\r\n<TD>1869</TD>\r\n<TD>+:-</TD>\r\n</TR>\r\n\r\n<TR>\r\n<TD>8</TD>\r\n<TD>Christof Walter</TD>\r\n<TD>1625</TD>\r\n<TD>-</TD>\r\n<TD>Herbert Birkle</TD>\r\n<TD>1501</TD>\r\n<TD>½-½</TD>\r\n</TR>\r\n\r\n<TR><TD colspan=7 bgcolor=000000></TD></TR>\r\n</TABLE>\r\n<BR><BR>Einen Bericht liefere ich an dieser Stelle noch nach, sobald ich die Informationen eingeholt habe.', 'Zu siebt holten wir gegen Gersthofen immerhin noch ein 4 : 4, vor allem auch wegen starker Leistung unserer beiden Spitzenbretter, die beide punkteten. An dieser Stelle wünschen wir Achim gute Besserung!', 403,  '3', 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
