		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (36, '2004-02-29', 'Bond von Richthofen', 'Fahndung nach Bondi - alias Bond v. Richthofen, der tollkühne F(li)eger', 'Joe Cantner', NULL, '<IMG SRC=http://www.schnuerer.com/~nightingale/skk_2003/pics/Wanted.jpg>', 'Fahndung nach Bondi - alias Bond v. Richthofen, der tollkühne F(li)eger. Anbei das dpa-Fahndungsfoto.', 285,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
