		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (41, '2004-03-07', 'Tabellenzweiter (wir) gegen Tabellendritten', 'Leichte Vorentscheidung - nix für schwache Nerven.', 'Eckhardt Frank', NULL, 'Mann, das war nix für schwache Nerven!\r<BR>Tabellenzweiter (wir) gegen Tabellendritten, das war eine leichte Vorentscheidung. \r<BR>Nach eineinhalb Stunden bekam ich Remisangebot (wenn man mal davon ausgeht, dass der Gegner keinen Quatsch am Brett fabriziert, dann zeichnete sich für mich nicht der Hauch eines Gewinnweges ab. Also drehte ich mal eine Zwischenrunde: Martin stand bereits kurz vor der Aufgabe, der hatte einen rabenschwarzen Tag erwischt; Christian hatte mehr oder weniger seine Eröffnung noch in Bearbeitung, die beiden Michaels standen durchwachsen, Sarna (vor dem Match: Sarna denk dran: wenn Du gewinnst, gewinnt die Mannschaft!) stand positionell a bisserl besser, Thomas hatte einen sicheren Mehrbauern bei aktiver Stellung und Stefan bringt früher oder später den Punkt. - Ok, Remis. \r<BR>\r<BR>Also ehrlich: Zuschauen kostet mich mehr Nerven als selber rumschieben! Michael K. hatte einen Wenigerbauern, den er aber erobern und zum Unentschieden abwickeln konnte. Bei Michael M. brach am Brett kurz Hektik aus: gerade noch gleichwertig, war auf einmal die Partie fort. Weil mer grad dabei waren, warf Thomas seine Vorteile mehr oder weniger weg, remis. 1,5 : 3,5 gegen uns, nicht schön. Christian schlitterte in eine Mörderzeitnot rein, nicht schön. Stefan übersah eine Riesenkombi (da wäre der Gegner einfach von der Platte geputzt gewesen), nicht schön. Sarna hatte ihren Gegner positionell weichgeknetet: Der zog nur noch schnell nicht mehr gut; in einer Stellung, in der Fritz die Ärmel hochkrempelt und die Stellung nur noch abwickelt - machte sie remis, nicht schön. \r<BR>\r<BR>Wir hatten keine Chance mehr, aber die nutzten wir: Erst verwandelte Christian Positionsvorteil ganz cool in Materialvorteil, und dann knetete Stefan seinen Gegner und setzte ihn mit einer guten Minute auf der Uhr matt. Klasse Leistung!\r<BR>\r<BR>Wir sind immer noch auf einem Aufstiegsplatz; au weia, die Kreisklasse 2 droht!\r<BR>\r<BR><TABLE BORDER=2 CELLPADDING=2 CELLSPACING=2 BGCOLOR=#C0C0C0 ><TR BGCOLOR=#00FFFF><TH> </TH><TH>TSV Steppach SAbt. II</TH><TH>1590</TH><TH>-</TH><TH>SK Kriegshaber IV</TH><TH>1515</TH><TH>4-4</TH></TR><TR BGCOLOR=#00FFFF><TD>1</TD><TD>Hans-Werner Miller</TD><TD>1683</TD><TD>-</TD><TD>Eckhardt Frank</TD><TD>1774</TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>2</TD><TD>Richard Fendt</TD><TD>1614</TD><TD>-</TD><TD>Christian Mayr</TD><TD>1565</TD><TD>0-1</TD></TR><TR BGCOLOR=#00FFFF><TD>3</TD><TD>Francisc Sas</TD><TD>1595</TD><TD>-</TD><TD>Martin Klinke</TD><TD>1610</TD><TD>1-0</TD></TR><TR BGCOLOR=#00FFFF><TD>4</TD><TD>Bruno Niemeyer</TD><TD>1574</TD><TD>-</TD><TD>Michael Kling</TD><TD>1467</TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>5</TD><TD>Peter Ebbrecht</TD><TD>1552</TD><TD>-</TD><TD>Sarna Hacker</TD><TD>1522</TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>6</TD><TD>Alfons Seitz</TD><TD>1616</TD><TD>-</TD><TD>Michael Mayr</TD><TD>1095</TD><TD>1-0</TD></TR><TR BGCOLOR=#00FFFF><TD>7</TD><TD>Winkle</TD><TD>1512</TD><TD>-</TD><TD>Thomas Schade</TD><TD>1437</TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>8</TD><TD>Kurt Kaus</TD><TD>1573</TD><TD>-</TD><TD>Stefan Schneider</TD><TD>1647</TD><TD>0-1</TD></TR></TABLE>', 'Leichte Vorentscheidung - nix für schwache Nerven.', 350,  '4', 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
