		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (47, '2004-03-30', 'Klarer Sieg gegen Siemens', 'Wenn es mal so richtig läuft, dann läufts einfach ...', 'Harald Gergen', NULL, 'Gegen die stark abstiegsgefährdeten Siemensianer mußte unbedingt noch ein Punkt her, damit der Klassenerhalt sicher geschafft war. Diese Aufgabe bewerkstelligten wir diesmal ganz souverän und schafften sogar einen 5-3 Sieg.\r\n<BR>\r\n<BR>Helmut Wolfsteiner, Helmut Schönau und Andreas Stör konnten dabei jeweils sehr sicher ihre Partien gewinnen. Nur Felix mußte diesmal als Einziger eine Niederlage hinnehmen. Die anderen Partien endeten Unentschieden.\r\n<BR>\r\n<BR><BR>\r\n<BR><BR><TABLE border=0 cellpadding=3 cellspacing=0><TR><TD colspan=7 bgcolor=000000></TD></TR><TR><TD colspan=7 bgcolor=ffffff>Einzelergebnisse</TD></TR><TR><TD colspan=7 bgcolor=000000></TD></TR><TR bgcolor=dddddd><TD></TD><TD width=150>SK Kriegshaber</TD><TD width=50></TD><TD></TD><TD width=150>Siemens München</TD><TD width=50></TD><TD width=80>5-3</TD></TR><TR><TD>1</TD><TD>Wolfsteiner, Helmut</TD><TD>2332</TD><TD>-</TD>\r\n<BR><TD>Epding, Olaf</TD><TD>2194</TD><TD>1-0</TD>\r\n<BR></TR><TR><TD>2</TD><TD>Shekhter, Dima</TD><TD>2123</TD><TD>-</TD><TD>Dr. Jörg, Harald</TD><TD>2212</TD><TD>1/2</TD></TR><TR><TD>3</TD><TD>Reis, Thomas</TD><TD>2156</TD><TD>-</TD><TD>Fauth, Dietmar</TD><TD>2202</TD><TD>1/2</TD></TR><TR><TD>4</TD><TD>Stör, Andreas</TD><TD>2128</TD><TD>-</TD>\r\n<BR><TD>Böhm, Uwe</TD><TD>2198</TD>\r\n<BR><TD>1-0</TD></TR><TR><TD>5</TD><TD>Hahn, Christoph</TD><TD>2056</TD><TD>-</TD>\r\n<BR><TD>Rüther, Rudolph</TD><TD>2163</TD>\r\n<BR><BR><BR><TD>1/2</TD></TR><TR><TD>6</TD><TD>Stelter, Felix</TD><TD>2115</TD><TD>-</TD>\r\n<BR><TD>Amelang, Thorsten</TD><TD>2068</TD>\r\n<BR><TD>0-1</TD></TR><TR><TD>7</TD><TD>Gergen, Harald</TD><TD>1987</TD><TD>-</TD><TD>Caspers, Christian</TD><TD>2056</TD><TD>1/2</TD>\r\n<BR></TR><TR><TD>8</TD><TD>Schönau, Helmut</TD><TD>2021</TD><TD>-</TD>\r\n<BR><TD>Osthoff, Christian</TD><TD>1863</TD><TD>1-0</TD>\r\n<BR></TR><TR><TD colspan=7 bgcolor=000000></TD></TR></TABLE>\r\n<BR><BR>', 'Mit einem 5-3 Sieg gegen Siemens München schafft unsere Erste Mannschaft wieder einen Sieg in der Landesliga Süd.', 318,  '1', 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
