		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (59, '2004-04-27', 'Schwäbische Jugendblitzmeisterschaft in Schwabmünchen', 'Valeria wird schwäbische U12-Mädchenmeisterin!', 'Peter Reichardt', NULL, 'Mit einer Wagenladung waren wir auch bei der Blitz-EM der Jugend in Schwabmünchen vertreten. \r<BR>Andreas wurde dabei guter Fünfter in der zusammengefassten U 20, U 18 Gruppe und Vizemeister bei der U 20!! Ein toller Erfolg!! \r<BR>\r<BR>Überragend das Abschneiden von Valeria!! \r<BR>Sie liess sich von drei Startniederlagen nicht entmutigen und holte sich verdient den Sieg in der Mädchenwertung und damit ihren ersten Pokal!! \r<BR>Wir gratulieren herzlich!! \r<BR>In der Gesamtwertung wurde sie 4. von 20! \r<BR>In derselben Gruppe zeigte auch Josef in seinem ersten Blitzturnier dass er mit kürzer werdenden Bedenkzeit besser zurechtkommt! \r<BR>Er zeigt auch bei den Trainingsabenden seine Zockerqualitäten, aber gepaart mit niveauvollem Schach!! \r<BR>In Schwabmünchen sprang diesmal ein ordentlicher siebter Platz heraus!! \r<BR>', 'Andreas Anhäuser (U20), Valeria Rusovski (U12w), Josef Hertel (U12) holen sehr gute Platzierungen', 241,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
