		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (62, '2004-04-27', 'Kriegshaber III verliert letzte Begegnung mit 3 - 5', 'Rochade Augsburg rettet mit diesem Sieg den Klassenerhalt', 'Eckhardt Frank', NULL, 'Peter Grabowski hatte scheints ein paar Stunden bei seinem Sohn Felix genommen, und irgendwie bekam ihm der neue Jugendstil gut: Statt Positionsspiel fiel Peter wieder ein, wo der König steht und den knetete er in flottem Angriffsspiel weich, mit allen Extras, einschließlich Figurenopfer. \r\n<BR>\r\n<BR>Das waren dann eigentlich auch schon die guten Nachrichten, der Rest ging eher abwärts: Die Remisen an den Spitzenbrettern für Peter und Thomas waren zwar angesichts ihrer Gegner durchaus ehrenhaft, aber eine sehr zähe Angelegenheit und die Gefahr, einen ganzen Punkt für uns einzufahren bestand da nie. Naja, Stefan stand eigentlich recht erfolgversprechend, machte dann etwas überraschend ebenfalls Remis. Detlef hätte gegen seinen Gegner schon a bisserl mehr rausholen können, allerdings nicht in der Stellung, bei der man sich dann auf gerechtes Remis einigte. Bei Achim war es schade, dass er eine eigentlich sehr ordentliche Saison nicht gebührend beenden konnte; manchmal ist einfach der Wurm drin, dann sieht man nix, und das war so eine Partie. Davon ließ sich dann auch Lothar anstecken; er kämpfte zwar noch länger, aber vergeblich. Auch Philipp fightete stundenlang bis zum grausamen Ende an einer schon länger sehr schlcht stehenden Stellung herum. \r\n<BR>\r\n<BR>Kampfgeist gut, Ergebnis verbesserungsfähig. \r\n<BR>\r\n<BR>Noch eine persönliche Anmerkung von mir: Ich freue mich, dass Rochade die Klasse gehalten hat, in der Kreis 2 hat diese Mannschaft nichtgs verloren. Glückwunsch zum Klassenerhalt. \r\n<BR><TABLE BORDER=2 CELLPADDING=2 CELLSPACING=2 BGCOLOR=C0C0C0><TR BGCOLOR=#00FFFF><TH>3</TH><TH>SK Kriegshaber III</TH><TH>1786</TH><TH></TH><TH>-</TH><TH>Rochade Augsburg</TH><TH></TH><TH></TH><TH>3-5</TH></TR><TR BGCOLOR=#00FFFF><TD>1</TD><TD>Reichardt, Peter</TD><TD>1799</TD><TD></TD><TD>-</TD><TD>Zimmermann</TD><TD></TD><TD></TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>2</TD><TD>Städele, Thomas</TD><TD>1879</TD><TD></TD><TD>-</TD><TD>Weichelt, Jens</TD><TD>2057</TD><TD></TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>3</TD><TD>Sepp,Lothar</TD><TD>1738</TD><TD></TD><TD>-</TD><TD>Schrell, Harald</TD><TD></TD><TD></TD><TD>0-1</TD></TR><TR BGCOLOR=#00FFFF><TD>4</TD><TD>Czaika, Detlef</TD><TD>1878</TD><TD></TD><TD>-</TD><TD>Gerten, Paul</TD><TD>1750</TD><TD></TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>5</TD><TD>Grabowski, Peter</TD><TD>1728</TD><TD></TD><TD>-</TD><TD>Siebenhüter</TD><TD></TD><TD></TD><TD>1-0</TD></TR><TR BGCOLOR=#00FFFF><TD>6</TD><TD>Kiechl, Stefan</TD><TD>1705</TD><TD></TD><TD>-</TD><TD>Feder, Juri</TD><TD></TD><TD></TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>7</TD><TD>Margraf, Achim</TD><TD>1869</TD><TD></TD><TD>-</TD><TD>Schroers, Uwe</TD><TD>1960</TD><TD></TD><TD>0-1</TD></TR><TR BGCOLOR=#00FFFF><TD>8</TD><TD>Albers, Philipp</TD><TD> </TD><TD></TD><TD>-</TD><TD>Eichhorn W.</TD><TD></TD><TD></TD><TD>0-1</TD></TR></TABLE>', 'Nachdem es für unsere Dritte nach Saisonhalbzeit praktisch um nichts mehr ging - Aufstieg war nach der Niederlage in Gersthofen perdu, Abstieg ging bei unserem Punktekonto auch nicht mehr - fehlte in den letzten Begegnungen irgendwie der letzte Biss.', 300,  '3', 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
