		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (137, '2005-01-28', 'SKK erweitert seinen Aktionsradius', 'Nach Radeln und Laufen gibt es neuerdings auch die Fraktion Wassersport ...', 'Joe Cantner', NULL, '<A HREF=../files/piratenfete.pdf><IMG SRC=../pics/pfeilrotaufgelb.gif BORDER=0> Bilder zur Piraten-Fete</A>', 'Nach Radeln und Laufen gibt es neuerdings auch die Fraktion Wassersport ...', 255,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
