		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (206, '2005-10-10', 'RAPID-Turnier Schwabmünchen 2005', 'Insgesamt war die Teilnehmerzahl etwas enttäuschend', 'Eckhardt Frank', NULL, 'Die Abschlusstabellen aller Altergruppen ist auf dieser Homepage unter Turniere zu finden, auf unserer Jugendhomepage www.skkjugend.de wird bald noch ein ausführlicher bericht mit Photos folgen. ', '89 Aktive in Schwabmünchen, davon 21 aus Kriegshaber. Insgesamt hatten wir mit Marvin in der U16 und Felix in der U8 zwei Leute auf dem Siegertreppchen (jeweils Platz 3). Dabei hängte Felix Vinzent (Platz 4) ab.', 444,  NULL, 'Jugend', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
