		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (217, '2005-11-05', 'Augsburger U10-Jugendmeisterschaft', 'Platz 1 - 3 geht an Raphael, Sascha und Felix', 'Eckhardt Frank', NULL, 'An dieser Stelle nur ganz kurz:\r<BR>\r<BR>Platz 1: Raphael Lämmchen\r<BR>Platz 2: Sascha Buchberger\r<BR>Platz 3: Felix Grabowski\r<BR>\r<BR>Vor allem Raphael muss seine Gegner geradezu in Grund und Boden gespielt haben. Ein ausführlicher Bericht demnächst auf unseren Jugendseiten. \r<BR>\r<BR>Glückwunsch an unsere drei Erstplatzierten, aber ebenso an alle anderen unserer Teilnehmer!', 'Die U-10 Jugendmeisterschaft war in jeder Hinsicht ein voller Erfolg für unsere Jugendabteilung, und zwar ebenso zahlen- wie ergebnismäßig.', 219,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
