		{
			echo mysql_error()."<BR>";
			echo $strSQL."<BR>";
			exit;
		}


		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (266, '2006-03-12', 'C-KIasse/ VI. Mannschaft verlor gegen Caissa III', NULL, 'Markus Buchberger', NULL, 'Tja, auch diesesmal hat´s nicht für einen Mannschaftssieg gereicht. Wir verloren gestern (11.03.) gegen die 3. Mannschaft von Caissa Augsburg mit 2:4.\r\n<BR>\r\n<BR>Einen Punkt holte Peter Grabowski nach Anwendung der 40.Zug-Regel und Ausnutzung der fast gesamten möglichen Spielzeit an Brett 1.\r\n<BR>\r\n<BR>Den zweiten Punkt holte uns Raphael Lämmchen,nach gerade mal 50 min Spielzeit, der uns an Brett 6 als Ersatzspieler aushalf.  Super Leistung!!!\r\n<BR>\r\n<BR>Bretter 2-5 gingen direkt an unseren Gegner.\r\n<BR>Im Einsatz war an Brett 2 David Schury, an Brett 3 Markus Buchberger, an Brett 4 Sascha Buchberger und am 5. Brett Marvin Meyke den wir vom Tapezieren erlöst haben.:):):)\r\n<BR>\r\n<BR>Aufstiegsmöglichkeiten sind wohl nicht mehr gegeben, aber wir haben Spaß am Spiel und unsere Jugendlichen kommen wunderbar zum anspruchsvollen Einsatz.', 'Tja, auch diesesmal hat´s nicht für einen Mannschaftssieg gereicht. Wir verloren gestern (11.03.) gegen die 3. Mannschaft von Caissa Augsburg mit 2:4.', 188,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
