		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (276, '2006-03-31', 'SKK on the rocks', 'Thommy und Joe auf Madeira', 'Joe Cantner', NULL, 'Ende Februar verbrachten die SKK-Kämpen Tommi und Joe eine Woche Trainingslager auf Madeira, um sich für die kommenden Sport-Events in 2006 (u.a. Landkreislauf) vorzubereiten. Die drauffolgende Email-Korrespondenz zwischen Joe und SKK-Starläufer Lance brachte es dann mit sich, eine der absolvierten Trainingseinheiten etwas genauer darzulegen. Hier nun für alle interessierten SKKler die sagenhafte -On the Rocks-Story!\r\n<BR>\r\n<BR>\r\n<A HREF=http://www.skk.prowide.de/skkneu/files/RS.pdf><IMG SRC=..pics/pfeilrotaufgelb.gif border=0> On-the-rocks-Story (pdf)</A><BR><BR>\r\n<A HREF=http://www.skk.prowide.de/skkneu/files/RS.mid><IMG SRC=../../pics/pfeilrotaufgelb.gif border=0> On-the-rocks-Sound-Track (mid)</A><BR><BR>\r\n<IMG SRC=http://www.skk.prowide.de/skkneu/files/RS.jpg>\r\n<BR>\r\n<BR>', 'Ende Februar verbrachten die SKK-Kämpen Tommi und Joe eine Woche Trainingslager auf Madeira, um sich für die kommenden Sport-Events in 2006 (u.a. Landkreislauf) vorzubereiten.', 217,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
