		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (299, '2006-09-26', 'Augsburger Kreiseinzelmeisterschaft 2006', 'Drei Teilnehmer aus Kriegshaber', 'Eckhardt Frank', NULL, 'Daniel Birth, Paul Demel und Stefan Kiechl vertreten Kriegshaber auf der KVA-Meisterschaft. Dabei starteten Stefan und Daniel mit Niederlagen in der ersten Runde. ', 'Mit einem etwas besseren Feld (nach den 14 Teilnehmern im Vorjahr) startete die KVA-Meisterschaft.', 206,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
