		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (341, '2007-01-10', 'Zollhauspokal, 5. Runde ist gelost', 'Endspurt- Leute!', 'Eckhardt Frank', NULL, 'Felix ist auf die Zielgerade eingebogen, führt weiterhin ohne jeden Punktverlust an. Ich werde das Turnier Ende Januar 2007 beenden, bis dahin bitte möglichst viele Partien noch nachspielen (und mir bitte auch melden!!!)\r<BR>\r<BR>', 'Alle 5 Runden vom Zollhauspokal sind jetzt ausgelost, also: alle offenen Partien nachspielen und mit Schmackes auf die 5. Runde losgehen.', 444,  NULL, 'Alle', 163, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
