		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (347, '2007-01-24', 'Augsburger Mannschaftspokal 2007', '1. Runde', 'Eckhardt Frank', NULL, 'Für diejenigen, welche die Ausschreibung und Auslosung auf der Seite des Kreisverbands noch nicht entdeckt haben sollten, hier nochmals der Hinweis / Aufruf. \r<BR>\r<BR>Kreispokal: \r<BR>Es haben 8 Mannschaften gemeldet, wir müssen in der ersten Runde in Mering antreten. \r<BR>Die Paarungen: \r<BR>Göggingen - Lechhausen\r<BR>Mering - Kriegshaber\r<BR>Caissa Augsburg - SG Augsburg\r<BR>Gersthofen - Rochade\r<BR>\r<BR>ABC-Pokal: \r<BR>Hier beginnen wir (das heißt die 5. / 6.Mannschaft + Ersatzspieler) mit einem Heimkampf gegen Gersthofen. Insgesamt sind auch hier 8 Mannschaftsmeldungen eingegangen. \r<BR>Kriegshaber - gersthofen\r<BR>Aichach - Kissing\r<BR>SG Augsburg - Lechhausen\r<BR>Mering - Caissa Augsburg\r<BR>', '1. Runde Kreispokal und 1. Runde ABC-Pokal', 613,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
