		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (351, '2007-02-04', 'SKK I verliert gegen Tabellenführer Rosenheim', 'Klassenerhalt in weite ferne gerückt', 'Thomas Reis', NULL, 'Nach einer erneuten, nahezu chancenlosen 2:6 Niederlage gegen Tabellenführer Pang Rosenheim, steht die erste Mannschaft des SK Kriegshaber nun scheinbar hoffnungslos mit 0:10 Punkten auf dem letzten Tabellenplatz.   \r\n<BR>\r\n<BR>Jetzt hilft nur noch ein <b>Wunder</b>!\r\n<BR>\r\n<BR>Die Ergebnisse gibt es unter:\r\n<a href=http://www.schachbund-bayern.de/ligabetrieb/landesliga-sued.html>\r\n<BR>www.schachbund-bayern.de/ligabetrieb/landesliga-sued.html</a>\r\n<BR>\r\n<BR>\r\nBeim nächsten Auswärtskampf gegen Bayerwald Regen am 25.Februar haben wir wohl unsere allerletzte Minimalchance im Kampf um den Klassenerhalt noch einmal \r\nzurückzukehren.', 'Nach einer erneuten, nahezu chancenlosen 2:6 Niederlage gegen Tabellenführer Pang Rosenheim, steht die erste Mannschaft des SK Kriegshaber nun scheinbar hoffnungslos mit 0:10 Punkten auf dem letzten Tabellenplatz.', 659,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
