		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (425, '2007-09-26', 'Jahreshauptversammlung verschoben', 'Neuer Termin ist Freitag, 12.10.07', 'Eckhardt Frank', NULL, 'Aus satzungstechnischen Gründen (sowie einem Zeitmanagement-Problem des ersten Vorsitzenden) muss die Jahreshauptversammlung (vom 28.9.07) auf den 12.10.07 verschoben werden. ', 'Aus satzungstechnischen Gründen (sowie einem Zeitmanagement-Problem des ersten Vorsitzenden) muss die Jahreshauptversammlung (vom 28.9.07) auf den 12.10.07 verschoben werden.', 517,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
