		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (456, '2007-12-09', 'Monatsblitz Dezember - Endstand', NULL, 'Lothar Weimer', NULL, '<TABLE BORDER=2 ALIGN=CENTER WIDTH=100% CELLPADDING=2 CELLSPACING=2 BGCOLOR=#DAA520 BORDERCOLOR=#000000>\r\n<BR><CAPTION>Monatsblitz Dezember - Endstand</CAPTION>\r\n<BR><TR BGCOLOR=FOE68C><TD COLSPAN=2><TH>1</TH><TH>2</TH><TH>3</TH><TH>4</TH><TH>5</TH><TH>6</TH><TH>7</TH><TH>8</TH><TH>Summe</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>1</TH><TH ALIGN=LEFT>Ostertag, Hans</TH><TD>X</TD><TD>0</TD><TD>0</TD><TD>1</TD><TD>1</TD><TD>1</TD><TD>1</TD><TD>1</TD><TH>5</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>2</TH><TH ALIGN=LEFT>Voß, Michael</TH><TD>1</TD><TD>X</TD><TD>½</TD><TD>½</TD><TD>1</TD><TD>1</TD><TD>0</TD><TD>1</TD><TH>5</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>3</TH><TH ALIGN=LEFT>Vuckovic, Aleksandar</TH><TD>1</TD><TD>½</TD><TD>X</TD><TD>1</TD><TD>1</TD><TD>1</TD><TD>1</TD><TD>1</TD><TH>6½</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>4</TH><TH ALIGN=LEFT>Bauer, Bernd</TH><TD>0</TD><TD>½</TD><TD>0</TD><TD>X</TD><TD>0</TD><TD>0</TD><TD>0</TD><TD>1</TD><TH>1½</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>5</TH><TH ALIGN=LEFT>Kaiser, Viktor</TH><TD>0</TD><TD>0</TD><TD>0</TD><TD>1</TD><TD>X</TD><TD>1</TD><TD>0</TD><TD>1</TD><TH>3</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>6</TH><TH ALIGN=LEFT>Sodbakhsh, Rosemarie</TH><TD>0</TD><TD>0</TD><TD>0</TD><TD>1</TD><TD>0</TD><TD>X</TD><TD>0</TD><TD>1</TD><TH>2</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>7</TH><TH ALIGN=LEFT>Stelter, Felix</TH><TD>0</TD><TD>1</TD><TD>0</TD><TD>1</TD><TD>1</TD><TD>1</TD><TD>X</TD><TD>1</TD><TH>5</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>8</TH><TH ALIGN=LEFT>Weimer, Lothar</TH><TD>0</TD><TD>0</TD><TD>0</TD><TD>0</TD><TD>0</TD><TD>0</TD><TD>0</TD><TD>X</TD><TH>0</TH></TR>\