		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (476, '2008-02-03', 'Monatsblitz Februar 2008', NULL, 'Lothar Weimer', NULL, '<TABLE BORDER=2 ALIGN=CENTER WIDTH=90% CELLPADDING=2 CELLSPACING=2 BGCOLOR=#DAA520 BORDERCOLOR=#000000>\r\n<BR><CAPTION>Monatsblitz Februar 2008 - Endstand</CAPTION>\r\n<BR><TR BGCOLOR=FOE68C><TD COLSPAN=2><TH>1</TH><TH>2</TH><TH>3</TH><TH>4</TH><TH>5</TH><TH>6</TH><TH>7</TH><TH>8</TH><TH>Punkte</TH><TH>Wertung</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>1</TH><TH ALIGN=LEFT>Stör, Andreas</TH><TD>X</TD><TD>1</TD><TD>1</TD><TD>1</TD><TD>0</TD><TD>1</TD><TD>1</TD><TD>1</TD><TH>6</TH><TH>19</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>2</TH><TH ALIGN=LEFT>Stubenrauch, Bruno</TH><TD>0</TD><TD>X</TD><TD>½</TD><TD>1</TD><TD>1</TD><TD>1</TD><TD>1</TD><TD>1</TD><TH>5½</TH><TH>14¾</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>3</TH><TH ALIGN=LEFT>Schneider, Stefan</TH><TD>0</TD><TD>½</TD><TD>X</TD><TD>0</TD><TD>1</TD><TD>0</TD><TD>1</TD><TD>1</TD><TH>3½</TH><TH>9¼</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>4</TH><TH ALIGN=LEFT>Buchert, Wolfgang</TH><TD>0</TD><TD>0</TD><TD>1</TD><TD>X</TD><TD>1</TD><TD>0</TD><TD>½</TD><TD>1</TD><TH>3½</TH><TH>8¾</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>5</TH><TH ALIGN=LEFT>Bauer, Bernd</TH><TD>1</TD><TD>0</TD><TD>0</TD><TD>0</TD><TD>X</TD><TD>1</TD><TD>0</TD><TD>1</TD><TH>3</TH><TH>10</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>6</TH><TH ALIGN=LEFT>Städele, Thomas</TH><TD>0</TD><TD>0</TD><TD>1</TD><TD>1</TD><TD>0</TD><TD>X</TD><TD>1</TD><TD>0</TD><TH>3</TH><TH>9½</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>7</TH><TH ALIGN=LEFT>Kaiser, Viktor</TH><TD>0</TD><TD>0</TD><TD>0</TD><TD>½</TD><TD>1</TD><TD>0</TD><TD>X</TD><TD>1</TD><TH>2½</TH><TH>5¾</TH></TR>\r\n<BR><TR BGCOLOR=FOE68C ALIGN=CENTER><TH>8</TH><TH ALIGN=LEFT>Cantner, Joe</TH><TD>0</TD><TD>0</TD><TD>0</TD><TD>0</TD><TD>0</TD><TD>1</TD><TD>0</TD><TD>X</TD><TH>1</TH><TH>3</TH></TR>\r\n<BR></TABLE>\r\n<BR>Das nächste Monatsblitzturnier findet am 7. März 2008 um 20.00 Uhr statt. Gäste aus anderen Vereinen sowie vereinslose Spieler sind jederzeit herzlich willkommen.', 'Monatsblitz vom 01.02.2008 - Endstand.', 403,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
