		{
			echo mysql_error()."<BR>";
			echo $strSQL."<BR>";
			exit;
		}


		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (482, '2008-02-10', 'Fortsetzung Zweite gegen Göggingen', NULL, 'Viktor Kaiser', NULL, 'Bruno hat letztendlich auch verloren. Er war aber mit seiner Partie trotzdem ganz zufrieden.\r<BR>Ich denke mal, dass Rosi, Helmut und Flo ziemlich gefrustet waren. Gerade wenn man wie Helmut und Flo sechs Stunden spielt ist eine Niederlage doppelt ärgerlich.\r<BR>Nächstes mal gehts dann gegen die SG Augsburg. Schau mer mal, ob uns dann etwas mehr gelingt.\r<BR>', NULL, 414,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
