		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (485, '2008-02-13', 'Gratulation an unseren Nachwuchs', 'Gute Leistung bei der Schwäbischen Jugendmeisterschaft', 'Eckhardt Frank', NULL, 'Ich empfehle einen Blick in Ergebnisse und Neuigkeiten auf unserer Jugendseite www.skkjugend.de. \r<BR>\r<BR>Danke an Karin Grabowski und Markus Buchberger, die täglich von der Front über Newsletter informierten. Klasse!', '9 Teilnehmer in Dinkelscherben, für die Bayerische haben sich doppelt so viele SpielerInnen wie im vergangenen Jahr qualifiziert.', 390,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
