		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (499, '2008-04-01', 'C-Klasse Augsburg', 'Ergebnisse', 'Eckhardt Frank', NULL, 'Fehlende Ergebnisse bitte gerne an mich  zumelden. \r\n<p>\r\nEinzelergebnisse aus der 6.Runde:\r\n<TABLE BORDER=2 CELLPADDING=2 CELLSPACING=2 BGCOLOR=#C0C0C0 ><TR BGCOLOR=#00FFFF><TH> </TH><TH>Kriegshaber VI</TH><TH> </TH><TH>-</TH><TH>Rochade Augsburg II</TH><TH> </TH><TH>2-4</TH></TR><TR BGCOLOR=#00FFFF><TD>1</TD><TD>Josef Hertl</TD><TD>1182</TD><TD>-</TD><TD>Bela Komani</TD><TD>1700</TD><TD>0-1</TD></TR><TR BGCOLOR=#00FFFF><TD>2</TD><TD>Markus Buchberger</TD><TD>843</TD><TD>-</TD><TD>Anton Siebenhüter</TD><TD>1609</TD><TD>0-1</TD></TR><TR BGCOLOR=#00FFFF><TD>3</TD><TD>Gerd Heigemeir</TD><TD>1382</TD><TD>-</TD><TD>Jiri Hyvnar</TD><TD>1412</TD><TD>+:-</TD></TR><TR BGCOLOR=#00FFFF><TD>4</TD><TD>Raphael Lämmchen</TD><TD>1009</TD><TD>-</TD><TD>Sabrina Schrell</TD><TD>----</TD><TD>0-1</TD></TR><TR BGCOLOR=#00FFFF><TD>5</TD><TD>Alexander Schönau</TD><TD>----</TD><TD>-</TD><TD>Sevkija Kadic</TD><TD>----</TD><TD>0-1</TD></TR><TR BGCOLOR=#00FFFF><TD>6</TD><TD>Tamara Buchberger</TD><TD>----</TD><TD>-</TD><TD>Peter Mayer</TD><TD>----</TD><TD>1-0</TD></TR><TABLE>\r\n\r\n<TABLE BORDER=2 CELLPADDING=2 CELLSPACING=2 BGCOLOR=#C0C0C0 ><TR BGCOLOR=#00FFFF><TH> </TH><TH>Rainer SC III</TH><TH> </TH><TH>-</TH><TH>Aichach III</TH><TH> </TH><TH>3-3</TH></TR><TR BGCOLOR=#00FFFF><TD>1</TD><TD>Bastian Wagner</TD><TD>1352</TD><TD>-</TD><TD>Karl Glas</TD><TD>1420</TD><TD>1-0</TD></TR><TR BGCOLOR=#00FFFF><TD>2</TD><TD>Alexander Wodstrschil</TD><TD>1481</TD><TD>-</TD><TD>Johann Kneifl</TD><TD>1601</TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>3</TD><TD>Moritz Holler</TD><TD>961</TD><TD>-</TD><TD>Heinz Neumeier</TD><TD>1493</TD><TD>½-½</TD></TR><TR BGCOLOR=#00FFFF><TD>4</TD><TD>Sebastian Meier</TD><TD>1113</TD><TD>-</TD><TD>Markus Bohn</TD><TD>1322<