		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (540, '2008-06-26', 'Zollhauspokal 2008', 'Turnierinformationen für die Teilnehmer des Zollhauspokals', 'Lothar Weimer', NULL, '<BR><TABLE BORDER=1 ALIGN=CENTER BORDERCOLOR=#000000 BGCOLOR=#DAA520 CELLSPACING=2 CELLPADDING=5>\r\n<BR><TH COLSPAN=4>Zollhauspokal 2008, Ergebnisse der 4. Runde</TH></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Paul Demel</TD><TD> - </TD><TD>Lothar Weimer</TD><TD>1 : 0</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Eckhardt Frank</TD><TD> - </TD> <TD>Viktor Kaiser</TD><TD>1 : 0</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Rosi Sodbakhsh</TD><TD> - </TD><TD>Thomas Städele</TD><TD>½ : ½</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>David Schury</TD><TD> - </TD> <TD>Peter Reichardt</TD><TD>0 : 1</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Valeria Rusovski</TD><TD> - </TD><TD>Sebastian Frank</TD><TD>- : +</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Sascha Buchberger</TD><TD> - </TD><TD><I>Freilos</I></TD><TD>+ : -</TD></TR>\r\n<BR></TABLE><P>\r\n<BR>Da Valeria inzwischen das zweite Mal eine Partie kampflos abgab, habe ich sie nunmehr aus dem Turnier genommen.<P>\r\n<BR>Der Zwischenstand nach 4 Runden sieht so aus:<P>\r\n<BR>\r\n<BR><TABLE BORDER=1 ALIGN=CENTER BGCOLOR=#DAA520 BORDERCOLOR=#000000 CELLSPACING=2 CELLPADDING=5>\r\n<BR><TH COLSPAN=5>Zollhauspokal 2008, Stand nach 4 von 7 Runden</TH></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD><I>Pl.</I></TD><TD ALIGN=LEFT><I>Spieler</I></TD><TD><I>Pkt.</I></TD><TD><I>Buchh.</I></TD><TD><I>BuSum</I></TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 1.</TD><TD ALIGN=LEFT>Frank, Eckhardt      </TD><TD>3½</TD><TD>8½</TD><TD>37</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 2.</TD><TD ALIGN=LEFT>Demel, Paul          </TD><TD>3</TD><TD>10½</TD><TD>31½</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 3.</TD><TD ALIGN=LEFT>Weimer, Lothar       </TD><TD>2½</TD><TD>9½</TD><TD>35½</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 4.</TD><TD ALIGN=LEFT>Reichardt, Peter     </TD><TD>2½</TD><TD>6½</TD><TD>31½</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 5.</TD><TD ALIGN=LEFT>Kaiser, Viktor       </TD><TD>2</TD><TD>8½</TD><TD>33</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 6.</TD><TD ALIGN=LEFT>Frank, Sebastian     </TD><TD>2</TD><TD>7½</TD><TD>27</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 7.</TD><TD ALIGN=LEFT>Sodbakhsh, Rosemarie </TD><TD>2</TD><TD>7</TD><TD>32</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 8.</TD><TD ALIGN=LEFT>Städele, Thomas </TD><TD>2</TD><TD>5½</TD><TD>21</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD> 9.</TD><TD ALIGN=LEFT>Buchberger, Sascha   </TD><TD>1½</TD><TD>8½</TD><TD>28</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD>10.</TD><TD ALIGN=LEFT>Schury, David        </TD><TD>1½</TD><TD>8</TD><TD>29</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C ALIGN=CENTER><TD>11.</TD><TD ALIGN=LEFT>Rusovski, Valeria (ausgeschieden)   </TD><TD>½</TD><TD>7½</TD><TD>28½</TD></TR>\r\n<BR></TABLE><P>\r\n<BR>\r\n<BR>Die 5. Runde ist am Freitag, dem 27. Juni um 20.00 Uhr zu spielen. Verlegungen sind nur nach vorheriger (!) Absprache mit dem Gegner möglich. Bitte bei Verlegungen daran denken, dass am folgenden Freitag wieder das Monatsblitz stattfindet.<P>\r\n<BR>\r\n<BR><TABLE BORDER=1 ALIGN=CENTER BORDERCOLOR=#000000 BGCOLOR=#DAA520 CELLSPACING=2 CELLPADDING=5>\r\n<BR><TH COLSPAN=4>Zollhauspokal 2008, Paarungen für die 5. Runde</TH></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Peter Reichardt</TD><TD> - </TD><TD>Eckhardt Frank</TD><TD>__:__</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Thomas Städele</TD><TD> - </TD> <TD>Paul Demel</TD><TD>__:__</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Lothar Weimer</TD><TD> - </TD><TD>Sebastian Frank</TD><TD>__:__</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Viktor Kaiser</TD><TD> - </TD> <TD>Sascha Buchberger</TD><TD>__:__</TD></TR>\r\n<BR><TR BGCOLOR=#FOE68C><TD>Rosemarie Sodbakhsh</TD><TD> - </TD><TD>David Schury</TD><TD>__:__</TD></TR>\r\n<BR></TABLE><P>\r\n<BR>\r\n<BR>Turniermodus:<BR> 7x CH-System, Bedenkzeit 2 Std./40 Züge + 30 min./Rest, Spielbeginn jeweils 20.00 Uhr, DWZ-Auswertung.<P>\r\n<BR>\r\n<BR><B><U>Die Termine der letzten drei Runden:</U></B><BR>\r\n<BR><UL>\r\n<BR><LI>5. Runde: Freitag, 27. Juni 2008\r\n<BR><LI>6. Runde: Freitag, 11. Juli 2008\r\n<BR><LI>7. Runde: Freitag, 25. Juli 2008\r\n<BR></UL><P>\r\n<BR>\r\n<BR>Ich wünsche Euch weiterhin viel Spaß und spannende Partien!', 'Ergebnisdienst für die 4. und Auslosung für die 5. Runde', 263,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
