		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (553, '2008-10-05', 'Schulungsabend f&uuml;r Mannschaftsf&uuml;hrer', 'Schulungsabend f&uuml;r Mannschaftsf&uuml;hrer', 'Lothar Weimer', NULL, 'F&uuml;r Mittwoch, den 15. Oktober 2008, habe ich ab 19.30 Uhr einen Schulungsabend f&uuml;r Mannschaftsf&uuml;hrer in unseren Klubr&auml;umen vorgesehen. Es geht vorwiegend um Regelkunde und darum, wie man bei Mannschaftsk&auml;mpfen auftretende Streitf&auml;lle oder Probleme korrekt l&ouml;st.<P>Ich empfehle allen unseren Mannschaftsf&uuml;hrern, daran teilzunehmen.', 'Regelkundeabend am 15.10.2008 im Klublokal', 126,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
