		$strSQL = "INSERT INTO skk_news (id, newsdate, headline, headline2, author, picture, text, shorttext, hits, newstable, category, contentid, createdate, creator) VALUES (567, '2008-10-26', 'Kurzmeldung unserer VI. Mannschaft aus der C-Klasse', 'Kurzmeldung unserer VI. Mannschaft aus der C-Klasse', 'Markus Buchberger', NULL, 'Die Ergebnisse im Einzelnen:\r<BR>\r<BR>SKK VI / SGA IV  3:3\r<BR>\r<BR>Brett 1: Messerschmidt / Buchberger Sascha 0:1\r<BR>Brett 2: Sorokin / Buchberger Markus 1/2\r<BR>Brett 3: Mesovaca / Fischer Josef 1:0\r<BR>Brett 4: Antic / Buchberger Tamara 1/2\r<BR>Brett 5: Janowitz D. / Kling Max 0:1\r<BR>Brett 6: Janowitz Z. / Straller Armin 1:0\r<BR>\r<BR>Und weiter geht´s schon am 15. November mit einem Heimspiel gegen Lechhausen.', 'SKK VI gegen SGA IV  3:3', 76,  NULL, 'Alle', NULL, '2008-12-05', 'ALTDATENÜBERNAHME PROWIDE');";
